import Button from "./components/Button/Button";
import Checkbox from "./components/Checkbox/Checkbox";
import Input from "./components/Input/Input";
import Tag from "./components/Tag/Tag";
import TaskCard from "./components/TaskCard/TaskCard";

const tagsArray = [
  { size: 'small', color: 'violet' },
  { size: 'small', color: 'green' },
  { size: 'small', color: 'red' },
  { size: 'small', color: 'orange' },
  { size: 'small', color: 'blue' },
  { size: 'small', color: 'light_green' },
  { size: 'small', color: 'dark_blue' },
  { size: 'small', color: 'yellow' },
]

export default function App() {
  return (
    <>
      <Tag color={'violet'} size={'large'} />
      <Tag color={'green'} size={'medium'} />
      <Tag color={'red'} size={'small'} />
      <Checkbox text={'Комментарий'} />
      <TaskCard text={'Нарисовать иллюстрации'} tagsArray={tagsArray} />
      <Button text={'+ Добавить тикет'} type={'add_big'} />
      <Button text={'Сохранить'} type={'save_big'} />
      <Button text={'Сохранить'} type={'save_small'} />
      <Button text={'Да'} type={'confirm'} />
      <Button text={'Добавить'} type={'add_small'} />
      <Input type={'input'} placeholder={'Название'}/>
    </>
  );
}
