import React from 'react';
import styles from './styles.module.css';
import { useField } from 'formik';

export default function Input({ placeholder, type, ...props }) {
  const [field, meta] = useField(props);

  return type === 'input' ?
    <input {...field} {...props} type="text" placeholder={placeholder} className={styles.input} /> :
    <textarea {...field} {...props} placeholder={placeholder} className={styles.textarea}></textarea>
}
