import React, { useState } from 'react'
import { Formik, Form } from 'formik';
import Input from '../Input/Input';
import Button from '../Button/Button';
import TagSelector from '../TagSelector/TagSelector';
import { database } from '../../assets/database';
import styles from './styles.module.css';
import { Link } from 'react-router-dom';

export default function TaskForm({ modalType, text, description, onClick }) {
  const [value, setValue] = useState([]);

  function submit() {

    onClick();
  }

  return (
    <Formik initialValues={{}} onSubmit={(values, { resetForm }) => {
      console.log(values);
      console.log(value);
      resetForm();
    }}>
      <Form>
        {modalType === 'create' &&
          <div className={styles.container}>
            <Input name='task' type='text' input={'input'} placeholder='Название' />
            <Input name='description' type='text' placeholder='Описание' />
            <TagSelector setValue={setValue} />
            <Button text={'Сохранить'} type={'save_big'} onClick={() => {}} />
          </div>
        }
        {modalType !== 'create' && modalType !== 'ticket' &&
          <div className={styles.container}>
            <Input name='task' type='text' input={'input'} placeholder='' />
            <Input name='description' type='text' placeholder='' />
            <TagSelector setValue={setValue} />
            <Button text={'Сохранить'} type={'save_big'} onClick={submit} />
          </div>
        }
        {modalType === 'ticket' &&
          <div className={styles.ticket_container}>
            <Input text={text} name='task' type='text' input={'input'} />
            <Input description={description} name='description' type='text' />
          </div>
        }

      </Form>
    </Formik>
  )
}
