import React from 'react'
import styles from './styles.module.css';
import dots from '../../assets/images/dots.png';
import message1 from '../../assets/images/message1.png';
import message2 from '../../assets/images/message2.png';
import Tag from '../../components/Tag/Tag.jsx';
import { v4 as uuidv4 } from 'uuid';

export default function TaskCard({ text, tagsArray, id, clickHandler }) {
  return (
    <div className={styles.container} onClick={clickHandler}>
      <div className={styles.top_container}>
        <p>{text}</p>
        <a href=""></a>
        <img src={dots} alt="" />
      </div>
      <div className={styles.bottom_container}>
        <div className={styles.tags_container}>
          {tagsArray?.map((tag) => <Tag color={tag} size={'small'} key={uuidv4()} />)}
        </div>
        <img src={message1} alt="message1" />
        <img src={message2} alt="message2" />
      </div>
    </div>
  )
}
