import React from 'react'

export default function Input({ placeholder, type }) {
  return type === 'input' ?
    <input type="text" placeholder={placeholder} className={'styles.input'}/> :
    <textArea placeholder={placeholder} className={'styles.textarea'}></textArea>
}
