import React, { useState } from 'react'
import { Formik, Form } from 'formik';
import Input from '../Input/Input';
import Button from '../Button/Button';
import TagSelector from '../TagSelector/TagSelector';
import { database } from '../../assets/database';
import styles from './styles.module.css';

export default function TaskForm() {
  const [value, setValue] = useState([]);

  return (
    <Formik initialValues={{}} onSubmit={(values, { resetForm }) => {
      console.log(values);
      resetForm();
    }}>
      <Form>
        <div className={styles.container}>
          <Input name='task' type='text' input={'input'} placeholder='Название' />
          <Input name='description' type='text' placeholder='Описание' />
          <div className=''></div>
          <TagSelector tagsArray={database.tagsArray} setValue={setValue} />
          <Button text={'Сохранить'} type={'save_big'} />
        </div>
      </Form>
    </Formik>
  )
}
