// ]

const test2 = 'create'

export default function Main() {
  let { value, toggle } = useToggle(false);
  let [modalType, setModalType] = useState('');

  function clickHandler(e) {
    // console.log(e.target);
    switch (e.target.textContent) {
      case '+ Добавить тикет':
        setModalType('create');
        break;
      // case 'delete':
      //   setModalType('delete');
      //   break;
      default:
        setModalType('delete');
        break;
    }
    toggle();
  }

  return (
    <div className={styles.container}>
      {modalType === 'create' && !!value && <Modal text={'Создать тикет'}>

        <img src={close} alt="" onClick={toggle} />
        <TagSelector tagsArray={database.tagsArray} />
        <Input type={'input'} placeholder={'Название'} />
        <Input placeholder={'Описание'} />
        <Button text={'Сохранить'} type={'save_big'} />

      </Modal>}
      {}
      <div className={styles.filter}>
        <Checkbox text={'Комментарий'} />
        <Checkbox text={'Описание'} />
        <Checkbox text={'Тег'} />
      </div>
      <div className={styles.task_container}>
        <div>
          <h2>Todo</h2>
          <div className={styles.todo_container}>
            {database.todo.map((task) => <TaskCard text={task.text} tagsArray={task.tagsArray} key={task.id} id={task.id} clickHandler={clickHandler} />)}
            <Button onClick={clickHandler} text={'+ Добавить тикет'} type={'add_big'} />
          </div>
        </div>
        <div>
          <h2>In progress</h2>
          <div className={styles.progress_container}>
            {database.progress.map((task) => <TaskCard text={task.text} tagsArray={task.tagsArray} key={task.id} id={task.id} clickHandler={clickHandler} />)}
            <Button text={'+ Добавить тикет'} type={'add_big'} onClick={clickHandler} />
          </div>
        </div>
        <div>
          <h2>Done</h2>
          <div className={styles.done_container}>
            {database.done.map((task) => <TaskCard text={task.text} tagsArray={task.tagsArray} key={task.id} id={task.id} />)}
          </div>
        </div>
      </div>
    </div>
  )
}
