import { useCallback, useState } from 'react'

export function useToggle(initialValue) {
  let [value, setValue] = useState(initialValue);

  const toggle = useCallback(
    () => { 
      setValue((currentValue) => !currentValue) },
    []
  );

  return { value, toggle }
}
