import React, { useState } from 'react'
import Button from '../../components/Button/Button';
import Checkbox from '../../components/Checkbox/Checkbox'
import Modal from '../../components/Modal/Modal';
import TaskCard from '../../components/TaskCard/TaskCard';
import { useToggle } from '../../hooks/useToggle';
import styles from './styles.module.css';
import close from '../../assets/images/close.png'
import Input from '../../components/Input/Input';
import TagSelector from '../../components/TagSelector/TagSelector';
import Comment from '../../components/Comment/Comment';

import { database } from '../../assets/database';
import TaskForm from '../../components/TaskForm/TaskForm';
import { useEffect } from 'react';

export default function Main() {
  let { value, toggle } = useToggle(false);
  const [modalType, setModalType] = useState('');
  const [tickets, setTickets] = useState([]);
  const [tags, setTags] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const response = await fetch('http://localhost:3030/', {
        credentials: 'include',
      });
      if (response.ok) {
        const data = await response.json();
        setTickets(data.tickets);
        setTags(data.tagsArray);
      }
    }

    fetchData();
  }, []);

  function clickHandler(e) {
    // console.log(e.target);
    switch (e.target.textContent) {
      case '+ Добавить тикет':
        setModalType('create');
        break;
      default:
        setModalType('delete');
        break;
    }
    toggle();
  }


  return (
    <div className={styles.container}>
      {modalType === 'create' && value && <Modal text={'Создать тикет'}>
        <img src={close} alt="" onClick={toggle} />
        {/* <TaskForm modalType={modalType} /> */}
      </Modal>}

      {modalType !== 'create' && value && <Modal text={'Редактировать'} tagsArray={tags.tagsArray}>
        <img src={close} alt="" onClick={toggle} />
        <TaskForm modalType={modalType} />
      </Modal>}

      <div className={styles.filter}>
        <Checkbox text={'Комментарий'} />
        <Checkbox text={'Описание'} />
        <Checkbox text={'Тег'} />
      </div>
      <div className={styles.task_container}>
        <div>
          <h2>Todo</h2>
          <div className={styles.todo_container}>
            {tickets?.filter((el) => el.status === 'Todo').map((task) => <TaskCard text={task.text} tagsArray={task.tagsArray} key={task.id} id={task.id} clickHandler={clickHandler} comments={task.comments} description={task.description} />)}
            <Button onClick={clickHandler} text={'+ Добавить тикет'} type={'add_big'} />
          </div>
        </div>
        <div>
          <h2>In progress</h2>
          <div className={styles.progress_container}>
            {tickets?.filter((el) => el.status === 'In progress').map((task) => <TaskCard text={task.text} tagsArray={task.tagsArray} key={task.id} id={task.id} clickHandler={clickHandler} comments={task.comments} description={task.description} />)}
            <Button text={'+ Добавить тикет'} type={'add_big'} onClick={clickHandler} />
          </div>
        </div>
        <div>
          <h2>Done</h2>
          <div className={styles.done_container}>
            {tickets?.filter((el) => el.status === 'Done').map((task) => <TaskCard text={task.text} tagsArray={task.tagsArray} key={task.id} id={task.id} clickHandler={clickHandler} comments={task.comments} description={task.description} />)}
          </div>
        </div>
      </div>
    </div>
  )
}
