import React from 'react';
import styles from './styles.module.css';
import { useField } from 'formik';
import { useState } from 'react';

export default function Input({ data, text, description, placeholder, input, ...props }) {
  const [field, meta] = useField(props);
  const [state, setState] = useState(data.text);

  if (text || description) {
    return input === 'input'
      ? <input {...field} {...props} placeholder={placeholder} className={styles.input} value={text} disabled="disabled" readOnly />
      : <textarea {...field} {...props} placeholder={placeholder} className={styles.textarea} value={description} disabled="disabled" readOnly />
  }

  if (data) {
    return input === 'input'
      ? <input {...field} {...props} placeholder={placeholder} className={styles.input} value={state} onChange={(e) => { setState(e.target.value) }} />
      : <textarea {...field} {...props} placeholder={placeholder} className={styles.textarea} value={field.value || data.description} onChange={(e)}/>
  }

  return input === 'input'
    ? <input {...field} {...props} placeholder={placeholder} className={styles.input} />
    : <textarea {...field} {...props} placeholder={placeholder} className={styles.textarea} />
}
