  { size: 'small', color: 'blue' },
  { size: 'small', color: 'light_green' },
  { size: 'small', color: 'dark_blue' },
  { size: 'small', color: 'yellow' },
]

export default function App() {
  return (
    <>
      <Checkbox text={'Комментарий'} />
      <TaskCard text={'Нарисовать иллюстрации'} tagsArray={tagsArraySmall} />
      <Button text={'+ Добавить тикет'} type={'add_big'} />
      <Button text={'Сохранить'} type={'save_big'} />
      <Button text={'Сохранить'} type={'save_small'} />
      <Button text={'Да'} type={'confirm'} />
      <Button text={'Добавить'} type={'add_small'} />
      <Input type={'input'} placeholder={'Название'} />
      <Input placeholder={'Описание'} />
      <TagSelector tagsArray={tagsArray} />
    </>
  );
}
