import React, { useState } from 'react'
import { Formik, Form } from 'formik';
import Input from '../Input/Input';
import Button from '../Button/Button';
import TagSelector from '../TagSelector/TagSelector';
import { database } from '../../assets/database';
import styles from './styles.module.css';

export default function TaskForm({ modalType, text, description }) {
  const [value, setValue] = useState([]);

  return (
    <Formik initialValues={{}} onSubmit={(values, { resetForm }) => {
      console.log(values);
      resetForm();
    }}>
      <Form>
        {modalType === 'create' &&
          <div className={styles.container}>
            <Input name='task' type='text' input={'input'} placeholder='Название' />
            <Input name='description' type='text' placeholder='Описание' />
            <TagSelector tagsArray={database.tagsArray} setValue={setValue} />
            <Button text={'Сохранить'} type={'save_big'} />
          </div>
        }
        {modalType === 'crфывыфвeate' &&
          <>
            <Input name='task' type='text' input={'input'} placeholder='' />
            <Input name='description' type='text' placeholder='' />
            <TagSelector tagsArray={database.tagsArray} setValue={setValue} />
            <Button text={'Сохранить'} type={'save_big'} />
          </>
        }
        {modalType === 'ticket' &&
          <>
            <Input text={text} name='task' type='text' input={'input'} />
            <div className={styles.ticket_container}>
              <Input description={description} name='description' type='text' />
            </div>
          </>
        }

      </Form>
    </Formik>
  )
}
