import React, { useState } from 'react';
import styles from './styles.module.css';
import returnImage from '../../assets/images/arrowBack.png';
import dotsImage from '../../assets/images/dots.png';
import Input from '../../components/Input/Input';
import Tag from '../../components/Tag/Tag';
import Comment from '../../components/Comment/Comment';
import Button from '../../components/Button/Button';
import Popup from '../../components/Popup/Popup';
import { useToggle } from '../../hooks/useToggle';
import Modal from '../../components/Modal/Modal';
import { database } from '../../assets/database';

export default function Ticket({ taskType }) {
  let popupObj = useToggle(false);
  let modalObj = useToggle(false);
  let popup = popupObj.value;
  let popupToggle = popupObj.toggle
  let modal = modalObj.value;
  let modalToggle = modalObj.toggle;
  // let { popup, popupToggle } = useToggle(false);
  // let { modal, modalToggle } = useToggle(false);
  let [modalType, setModalType] = useState('');

  function clickHandler(e) {
    console.log(e.target);
    switch (e.target.textContent) {
      case 'Удалить':
        setModalType('delete');
        break;
      default:
        setModalType('create');
        break;
    }

    popupToggle();
  }

  return (
    <div className={styles.container}>
      {modalType !== 'create' && modal && <Modal text={'Удалить тикет?'} tagsArray={database.tagsArray}>
        <Button text={'Да'} type={'confirm'} onClick={modalToggle} />
        <Button text={'Нет'} type={'confirm'} onClick={modalToggle} />
      </Modal>}
      <div className={styles.return}>
        <img src={returnImage} alt="" />
        <p>Вернуться к задачам</p>
      </div>
      <div className={styles.taskType}>
        <h2>{taskType}</h2>
        <div className={styles.popup_container}>
          <img src={dotsImage} alt="" onClick={popupToggle} />
          {popup && <Popup onClick={popupToggle} clickHandler={clickHandler} />}
        </div>
      </div>
      <div className={styles.task_container}>
        <Input type={'input'} />
        <Input />
        <div className={styles.bottom_container}>
          <div className={styles.tag_container}>
            <Tag color="violet" size="medium" />
            <Tag color="green" size="medium" />
            <Tag color="red" size="medium" />
            <Tag color="orange" size="medium" />
            <Tag color="blue" size="medium" />
            <Tag color="light_green" size="medium" />
            <Tag color="dark_blue" size="medium" />
            <Tag color="yellow" size="medium" />
          </div>
          <Comment author="Иванов Иван" text="Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. " />
          <Comment author="Иванов Иван" text="Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. " />
          <Comment author="Иванов Иван" text="Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. " />
          <Button text="добавить комментарий" type="commentAdd" />
        </div>
        <Button text="Сохранить" type="save_small" />
      </div>
    </div>
  );
}
