import React, { useMemo } from 'react'
import { ModalContainer } from '../ModalContainer/ModalContainer'
import Tag from '../Tag/Tag';
import styles from './styles.module.css';
import { v4 as uuidv4 } from 'uuid';
import { Formik, Form } from 'formik';


export default function Modal({ children, text, tagsArray }) {
  const childrenObj = (function createChildrenObj(children) {
    const result = {};
    console.log(children);

    children.forEach((el) => {
      if (el.type.name) {
        if (!result[el.type.name]) result[el.type.name] = [el];
        else result[el.type.name].push(el);
      } else {
        result[el.type] = el;
      }
    })

    return result;
  })(children)

  return (
    <ModalContainer>
      <div className={styles.overlay}>
        {childrenObj?.Button[0]?.props?.type === 'confirm' ?
          <div className={styles.modal2}>
            <h3>{text}</h3>
            <div className={styles.flex}>
              {childrenObj?.Button[0]}
              {childrenObj?.Button[1]}
            </div>
          </div>
          :
          <div className={styles.modal1}>
            <div className={styles.image}>
              {childrenObj?.img}
            </div>
            <h3>{text}</h3>
            <div className={styles.content}>
              <Formik initialValues={{}} onSubmit={(values, { resetForm }) => {
                console.log(values);
                resetForm();
              }}>
                <Form>
                  {childrenObj?.Input}
                  {childrenObj?.TagSelector}
                </Form>
              </Formik>
              {!!tagsArray && <div className={styles.tagsArray}>
                {tagsArray?.map((tag) => <Tag size={'medium'} color={tag} key={uuidv4()} />)}
              </div>}
              {childrenObj?.Comment}
            </div>
          </div>
        }
      </div>
    </ModalContainer>
  )
}
