/* eslint-disable no-undef */
import React, { useState } from 'react'
import { Formik, Form } from 'formik';
import Input from '../Input/Input';
import Button from '../Button/Button';
import TagSelector from '../TagSelector/TagSelector';
import styles from './styles.module.css';
import { Link, useNavigate } from 'react-router-dom';
import Tag from '../Tag/Tag';
import { v4 as uuidv4 } from 'uuid';

export default function TaskForm({ modalType, text, description, onClick, dataId }) {
  const [value, setValue] = useState([]);
  const navigate = useNavigate();
  const [ticketInfo, setTicketInfo] = useState({});

  async function fetchId() {
    const response = await fetch(`http://localhost:3030/${id}`, {
      credentials: 'include',
    });
    if (response.ok) {
      const data = await response.json();
      setTicketInfo(data);
    }
  }

  function submit(type) {
    switch (type) {
      case 'create':
        navigate('/')
        break;
      case 'edit':
        navigate('/')
        break;

      default:
        break;
    }
    // onClick();
  }

  async function createTicket() {
    const response = await fetch(`http://localhost:3030/${id}`, {
      credentials: 'include',
    });
    if (response.ok) {
      const data = await response.json();
      setTicketInfo(data);
    }
  }

  return (
    <Formik initialValues={{ task: data.text, description: data.description }} onSubmit={(values, { resetForm }) => {
      console.log(values);
      console.log(value);
      createTicket();
      resetForm();
    }}>
      <Form>
        {modalType === 'create' &&
          <div className={styles.container}>
            <Input name='task' type='text' input={'input'} placeholder='Название' />
            <Input name='description' type='text' placeholder='Описание' />
            <TagSelector setValue={setValue} />
            <Button text={'Сохранить'} type={'save_big'} onClick={() => { submit('create') }} />
          </div>
        }
        {modalType === 'edit' &&
          <div className={styles.container}>
            <Input name='task' type='text' input={'input'} placeholder='' data={data} />
            <Input name='description' type='text' placeholder='' data={data} />
            <div className={styles.tagsArray}>
              {data?.tagsArray?.map((tag) => <Tag color={tag} size={'medium'} key={uuidv4()} />)}
            </div>
            <TagSelector setValue={setValue} />
            <Button text={'Сохранить'} type={'save_big'} onClick={() => { submit('edit') }} />
          </div>
        }
        {modalType === 'ticket' &&
          <div className={styles.ticket_container}>
            <Input text={text} name='task' type='text' input={'input'} />
            <Input description={description} name='description' type='text' />
          </div>
        }

      </Form>
    </Formik>
  )
}
