import React from 'react';
import styles from './styles.module.css';
import { useField } from 'formik';

export default function Input({ placeholder, input, ...props }) {
  if (text || description) {
    return type === 'textArea'
      ? <textarea placeholder={placeholder} className={styles.textArea} value={description} disabled="disabled" readOnly />
      : <input placeholder={placeholder} className={styles.input} value={text} disabled="disabled" readOnly />;
  }
  const [field, meta] = useField(props);

  return input === 'input' ?
    <input {...field} {...props} type="text" placeholder={placeholder} className={styles.input} /> :
    <textarea {...field} {...props} placeholder={placeholder} className={styles.textarea}></textarea>
}
